import React, { useState } from 'react';
import './Tablet.css';

const Tablet = () => {
  const [showQR, setShowQR] = useState(false);

  return (
    <div className="tablet-container">
      {/* LIGNE 1: AGB */}
      <div className="line-1">AGB</div>

      {/* LIGNE 2: Welcome */}
      <div className="line-2">Welcome to AGB/Bienvenue chez AGB</div>

      {/* LIGNE 3: FRENAR */}
      <div className="line-3">FRENAR</div>

      {/* LIGNE 4: Please select */}
      <div className="line-4">Please select your service/Veuillez choisir votre service</div>

      {/* LIGNE 5: Cash Operations */}
      <div className="line-5">Cash Operations</div>

      {/* LIGNE 6: Verser/Retirer */}
      <div className="line-6">Verser/Retirer</div>

      {/* LIGNE 7: Paper Ticket */}
      <div className="line-7">Paper Ticket</div>

      {/* LIGNE 8: TicketPapier */}
      <div className="line-8">TicketPapier</div>

      {/* LIGNE 9: Virtual Ticket/ et Customer Service sur la même ligne */}
      <div className="line-9">
        <span className="virtual-ticket">Virtual Ticket/</span>
        <span className="customer-service">Customer Service</span>
      </div>

      {/* LIGNE 10: TicketVirtuel et Conseiller Client sur la même ligne */}
      <div className="line-10">
        <span className="ticket-virtuel">TicketVirtuel</span>
        <span className="conseiller-client">Conseiller Client</span>
      </div>

      {/* LIGNE 11: Scan this code to access et Corporate/VIP sur la même ligne */}
      <div className="line-11">
        <span className="scan-code">Scan this code to access</span>
        <span className="corporate-vip">Corporate/VIP</span>
      </div>

      {/* LIGNE 12: QONNEXEA anl tatackyour turn et Entreprises sur la même ligne */}
      <div className="line-12">
        <span className="qonnexea-text">QONNEXEA anl tatackyour turn</span>
        <span className="entreprises">Entreprises</span>
      </div>

      {/* LIGNE 13: in real-time et Cards &Digital sur la même ligne */}
      <div className="line-13">
        <span className="real-time">in real-time.</span>
        <span className="cards-digital">Cards &Digital</span>
      </div>

      {/* LIGNE 14: Cartes&E-Banking (aligné à droite) */}
      <div className="line-14">Cartes&E-Banking</div>

      {/* Bouton QR Code */}
      <div className="qr-section">
        <button className="qr-button" onClick={() => setShowQR(!showQR)}>
          Show QR Code
        </button>
        {showQR && (
          <div className="qr-display">
            <img 
              src={`https://api.qrserver.com/v1/create-qr-code/?size=120x120&data=${window.location.origin}/qonnexea`}
              alt="QR Code"
            />
          </div>
        )}
      </div>

      {/* Bouton Cancel/Return */}
      <button className="cancel-button">Cancel/Return</button>
    </div>
  );
};

export default Tablet;
