import React, { useState } from 'react';
import './Tablet.css';

const Tablet = () => {
  const [showQR, setShowQR] = useState(false);

  return (
    <div className="tablet-container">
      {/* LIGNE 1: AGB */}
      <div className="line1">AGB</div>

      {/* LIGNE 2: Welcome */}
      <div className="line2">Welcome to AGB/Bienvenue chez AGB</div>

      {/* LIGNE 3: FRENAR (sans séparateurs) */}
      <div className="line3">FRENAR</div>

      {/* LIGNE 4: Please select */}
      <div className="line4">Please select your service/Veuillee choisir votre service</div>

      {/* LIGNE 5: Cash Operations */}
      <div className="line5">Cash Operations</div>

      {/* LIGNE 6: Verser/Retirer */}
      <div className="line6">Verser/Retirer</div>

      {/* LIGNE 7: Paper Ticket */}
      <div className="line7">Paper Ticket</div>

      {/* LIGNE 8: TicketPalier */}
      <div className="line8">TicketPalier</div>

      {/* LIGNE 9: Virtual Ticket/ et Customer Service sur la même ligne */}
      <div className="line9">
        <span className="virtual">Virtual Ticket/</span>
        <span className="customer">Customer Service</span>
      </div>

      {/* LIGNE 10: TicketVirtrel et Conseller Client sur la même ligne */}
      <div className="line10">
        <span className="ticketvirtrel">TicketVirtrel</span>
        <span className="conseiller">Conseller Client</span>
      </div>

      {/* LIGNE 11: Scan this code to access et Corporate/VIP sur la même ligne */}
      <div className="line11">
        <span className="scan">Scan this code to access</span>
        <span className="corporate">Corporate/VIP</span>
      </div>

      {/* LIGNE 12: QONNEXEA anl tatackyour turn et Enterprises sur la même ligne */}
      <div className="line12">
        <span className="qonnexea">QONNEXEA anl tatackyour turn</span>
        <span className="enterprises">Enterprises</span>
      </div>

      {/* LIGNE 13: in real-time. et Cards &Digital sur la même ligne */}
      <div className="line13">
        <span className="realtime">in real-time.</span>
        <span className="cards">Cards &Digital</span>
      </div>

      {/* LIGNE 14: Cartes&E-Banking */}
      <div className="line14">Cartes&E-Banking</div>

      {/* Bouton QR Code */}
      <button className="qr-btn" onClick={() => setShowQR(!showQR)}>
        Show QR Code
      </button>

      {showQR && (
        <div className="qr-code">
          <img 
            src={`https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=${window.location.origin}/qonnexea`}
            alt="QR Code"
          />
        </div>
      )}

      {/* Bouton Cancel/Return */}
      <button className="cancel-btn">Cancel/Return</button>
    </div>
  );
};

export default Tablet;
