import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ticketService from '../services/ticketService';
import './Qonnexea.css';

const Qonnexea = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    total_waiting: 0,
    active_counters: 0,
    serving_now: 0,
    served_today: 0
  });
  const [ticketNumber, setTicketNumber] = useState('');
  const [trackingTicket, setTrackingTicket] = useState(null);
  const [showTracking, setShowTracking] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [notifications, setNotifications] = useState([
    { id: 1, message: "Your turn is approaching. Only 6 people ahead of you.", time: "20:36" },
    { id: 2, message: "Ticket T2466 generated for Loan Request. Your position: #12", time: "19:00" },
    { id: 3, message: "Thank you for your patience. Please fill out our satisfaction survey", time: "19:15" },
    { id: 4, message: "Ticket T6457 generated for Account Opening. Your position: #5", time: "19:15" }
  ]);

  useEffect(() => {
    fetchStats();
    const interval = setInterval(fetchStats, 10000);
    return () => clearInterval(interval);
  }, []);

  const fetchStats = async () => {
    try {
      const data = await ticketService.getQueueStats();
      setStats({
        total_waiting: data.total_waiting || 8,
        active_counters: 3,
        serving_now: 2,
        served_today: 45
      });
    } catch (err) {
      console.error('Erreur stats:', err);
      setStats({
        total_waiting: 8,
        active_counters: 3,
        serving_now: 2,
        served_today: 45
      });
    }
  };

  const handleTrackQueue = async (e) => {
    e.preventDefault();
    if (!ticketNumber.trim()) return;

    try {
      const ticket = await ticketService.trackTicket(ticketNumber);
      setTrackingTicket(ticket);
    } catch (err) {
      alert('Ticket non trouv√©');
    }
  };

  // Fonction pour ajouter une notification (exemple)
  const addNotification = (message) => {
    const newNotification = {
      id: notifications.length + 1,
      message,
      time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
    setNotifications([newNotification, ...notifications.slice(0, 9)]);
  };

  return (
    <div className="qonnexea-container">
      {/* En-t√™te */}
      <div className="qonnexea-header">
        <h1 className="qonnexea-logo">QONNEXEA</h1>
        <p className="agency-name">Agency: Algiers Main</p>
      </div>

      {/* Message de bienvenue */}
      <div className="welcome-message">
        <h2>Welcome to QONNEXEA</h2>
        <p>How can we help you today?</p>
      </div>

      {/* Actions principales */}
      <div className="main-actions">
        <button className="action-btn primary" onClick={() => navigate('/create-ticket')}>
          + Create Virtual Ticket
        </button>
        <button className="action-btn secondary" onClick={() => setShowTracking(!showTracking)}>
          Track My Queue
        </button>
      </div>

      {/* Formulaire de suivi */}
      {showTracking && (
        <div className="tracking-form">
          <form onSubmit={handleTrackQueue}>
            <input
              type="text"
              placeholder="Enter your ticket number (e.g., A45)"
              value={ticketNumber}
              onChange={(e) => setTicketNumber(e.target.value.toUpperCase())}
            />
            <button type="submit">Track</button>
          </form>
        </div>
      )}

      {/* Affichage du ticket suivi */}
      {trackingTicket && (
        <div className="tracking-result">
          <h3>Ticket {trackingTicket.number}</h3>
          <div className="tracking-details">
            <p><strong>Service:</strong> {trackingTicket.service}</p>
            <p><strong>Created at:</strong> {new Date(trackingTicket.created_at).toLocaleTimeString()}</p>
            <p><strong>Estimated wait:</strong> 12 minutes</p>
            <p><strong>Position:</strong> 5th</p>
            <p><strong>Ahead of you:</strong> 4 people</p>
          </div>
        </div>
      )}

      {/* Section principale avec deux colonnes */}
      <div className="main-content">
        {/* Colonne gauche - Tracking */}
        <div className="left-column">
          <h3>Tracking the Queue</h3>

          {trackingTicket ? (
            <div className="queue-position">
              <div className="position-details">
                <p><strong>Your position:</strong> 18</p>
                <p><strong>People ahead of you:</strong> 5</p>
                <p><strong>Estimated wait time:</strong> 20 minutes</p>
              </div>

              <div className="ticket-highlight">
                <span className="ticket-number">{trackingTicket.number}</span>
                <p>Please wait for your number to be called.</p>
              </div>

              <table className="queue-table">
                <thead>
                  <tr>
                    <th>Service</th>
                    <th>Position #1</th>
                    <th>Estimated Time</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Account Opening</td>
                    <td>#1</td>
                    <td>0 min</td>
                  </tr>
                </tbody>
              </table>
            </div>
          ) : (
            <div className="no-ticket">
              <p>Enter your ticket number above to track your position</p>
            </div>
          )}
        </div>

        {/* Colonne droite - Notifications */}
        <div className="right-column">
          <h3>Notifications</h3>
          <div className="notifications-list">
            {notifications.map(notif => (
              <div key={notif.id} className="notification-item">
                <p className="notification-message">{notif.message}</p>
                <span className="notification-time">{notif.time}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Section d'assistance */}
      <div className="assistance-section">
        <h3>Need Assistance?</h3>
        <div className="assistance-links">
          <button onClick={() => navigate('/support')}>üí¨ Chat with QONNEXEA Support</button>
          <button onClick={() => navigate('/faq')}>‚ùì Browse FAQs</button>
          <button onClick={() => navigate('/services')}>üè¢ View Branch Services</button>
          <button className="new-ticket-btn" onClick={() => navigate('/create-ticket')}>üé´ Get New Ticket</button>
        </div>
      </div>

      {/* Bouton Cancel */}
      <button className="cancel-btn" onClick={() => navigate('/')}>
        Cancel
      </button>
    </div>
  );
};

export default Qonnexea;
