import React, { useState } from 'react';
import './Tablet.css';

const Tablet = () => {
  const [showQR, setShowQR] = useState(false);

  return (
    <div className="tablet-container">
      {/* LIGNE 1: AGB */}
      <div className="line-1">AGB</div>

      {/* LIGNE 2: Welcome */}
      <div className="line-2">Welcome to AGB/Bienvenue chez AGB</div>

      {/* LIGNE 3: FRENAR */}
      <div className="line-3">FRENAR</div>

      {/* LIGNE 4: Please select */}
      <div className="line-4">Please select your service/Veuillez choisir votre service</div>

      {/* Main content with two columns */}
      <div className="main-content">
        {/* LEFT COLUMN */}
        <div className="left-column">
          <div className="cash-ops">
            <div className="cash-ops-main">Cash Operations</div>
            <div className="cash-ops-sub">Verser/Retirer</div>
          </div>

          <div className="paper-ticket">
            <div className="paper-ticket-main">Paper Ticket</div>
            <div className="paper-ticket-sub">TicketPapier</div>
          </div>

          <div className="virtual-ticket">
            <div className="virtual-ticket-main">Virtual Ticket/</div>
            <div className="virtual-ticket-sub">TicketVirtuel</div>
          </div>
        </div>

        {/* RIGHT COLUMN */}
        <div className="right-column">
          <div className="customer-service">
            <div className="customer-service-main">Customer Service</div>
            <div className="customer-service-sub">Conseiller Client</div>
          </div>

          <div className="corporate">
            <div className="corporate-main">Corporate/VIP</div>
            <div className="corporate-sub">Entreprises</div>
          </div>

          <div className="cards">
            <div className="cards-main">Cards & Digital</div>
            <div className="cards-sub">Cartes & E-Banking</div>
          </div>
        </div>
      </div>

      {/* SCAN CODE SECTION */}
      <div className="scan-section">
        <span className="scan-text">Scan this code to access</span>
        <button className="qr-button" onClick={() => setShowQR(!showQR)}>
          Show QR Code
        </button>
      </div>

      {/* QR CODE DISPLAY */}
      {showQR && (
        <div className="qr-display">
          <img 
            src={`https://api.qrserver.com/v1/create-qr-code/?size=120x120&data=${window.location.origin}/qonnexea`}
            alt="QR Code"
          />
          <div className="qonnexea-text">QONNEXEA anl tatackyour turn</div>
        </div>
      )}

      {/* IN REAL-TIME */}
      <div className="real-time">in real-time.</div>

      {/* CANCEL BUTTON */}
      <button className="cancel-button">Cancel/Return</button>
    </div>
  );
};

export default Tablet;
