import React, { useState } from 'react';
import './Tablet.css';

const Tablet = () => {
  const [showQR, setShowQR] = useState(false);

  return (
    <div className="tablet-container">
      {/* Header with AGB and Welcome */}
      <div className="header">
        <div className="line-1">AGB</div>
        <div className="line-2">Welcome to AGB/Bienvenue chez AGB</div>
      </div>

      {/* FRENAR */}
      <div className="line-3">FRENAR</div>

      {/* Please select */}
      <div className="line-4">Please select your service/Veuillez choisir votre service</div>

      {/* Main content grid */}
      <div className="content-grid">
        {/* Left column - Services */}
        <div className="left-column">
          <div className="service-item main-service">Cash Operations</div>
          <div className="service-item sub-service">Verser/Retirer</div>

          <div className="service-item">Paper Ticket</div>
          <div className="service-item sub-service">TicketPapier</div>

          <div className="service-item">Virtual Ticket/</div>
          <div className="service-item sub-service">TicketVirtuel</div>
        </div>

        {/* Right column - Customer options */}
        <div className="right-column">
          <div className="service-item main-service right-align">Customer Service</div>
          <div className="service-item sub-service right-align">Conseiller Client</div>

          <div className="service-item main-service right-align">Corporate/VIP</div>
          <div className="service-item sub-service right-align">Entreprises</div>

          <div className="service-item main-service right-align">Cards & Digital</div>
          <div className="service-item sub-service right-align">Cartes & E-Banking</div>
        </div>
      </div>

      {/* Scan code section */}
      <div className="scan-section">
        <div className="scan-text">Scan this code to access</div>
        <div className="qr-button-container">
          <button className="qr-button" onClick={() => setShowQR(!showQR)}>
            {showQR ? 'Hide QR Code' : 'Show QR Code'}
          </button>
        </div>
      </div>

      {/* QR Code display */}
      {showQR && (
        <div className="qr-display">
          <img 
            src={`https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=${window.location.origin}/qonnexea`}
            alt="QR Code"
          />
          <div className="qr-label">QONNEXEA anl atatckyour turn</div>
        </div>
      )}

      {/* Bottom section with in real-time */}
      <div className="bottom-section">
        <div className="real-time-text">in real-time.</div>
      </div>

      {/* Cancel/Return button */}
      <button className="cancel-button">Cancel/Return</button>
    </div>
  );
};

export default Tablet;
