import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ticketService from '../services/ticketService';
import './Qonnexea.css';

const Qonnexea = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    total_waiting: 0,
    active_counters: 0,
    serving_now: 0,
    served_today: 0
  });
  const [ticketNumber, setTicketNumber] = useState('');
  const [trackingTicket, setTrackingTicket] = useState(null);
  const [showTracking, setShowTracking] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [notifications, setNotifications] = useState([
    { id: 1, message: "Your turn is approaching. Only 6 people ahead of you.", time: "20:36" },
    { id: 2, message: "Ticket T2466 generated for Loan Request. Your position: #12", time: "19:00" },
    { id: 3, message: "Thank you for your patience. Please fill out our satisfaction survey", time: "19:15" },
    { id: 4, message: "Ticket T6457 generated for Account Opening. Your position: #5", time: "19:15" }
  ]);

  useEffect(() => {
    fetchStats();
    const interval = setInterval(fetchStats, 10000);
    return () => clearInterval(interval);
  }, []);

  const fetchStats = async () => {
    try {
      const data = await ticketService.getQueueStats();
      setStats({
        total_waiting: data.total_waiting || 8,
        active_counters: 3,
        serving_now: 2,
        served_today: 45
      });
    } catch (err) {
      console.error('Erreur stats:', err);
      // Donn√©es par d√©faut
      setStats({
        total_waiting: 8,
        active_counters: 3,
        serving_now: 2,
        served_today: 45
      });
    }
  };

  const handleTrackQueue = async (e) => {
    e.preventDefault();
    if (!ticketNumber.trim()) return;

    try {
      const ticket = await ticketService.trackTicket(ticketNumber);
      setTrackingTicket(ticket);
    } catch (err) {
      alert('Ticket non trouv√©');
    }
  };

  return (
    <div className="qonnexea-container">
      {/* En-t√™te */}
      <div className="qonnexea-header">
        <h1 className="qonnexea-logo">QONNEXEA</h1>
        <p className="agency-name">Agency: Algiers Main</p>
      </div>

      {/* Message de bienvenue */}
      <div className="welcome-message">
        <h2>Welcome to QONNEXEA</h2>
        <p>How can we help you today?</p>
      </div>

      {/* Actions principales */}
      <div className="main-actions">
        <button className="action-btn primary" onClick={() => navigate('/create-ticket')}>
          + Create Virtual Ticket
        </button>
        <button className="action-btn secondary" onClick={() => setShowTracking(!showTracking)}>
          Track My Queue
        </button>
      </div>

      {/* Formulaire de suivi */}
      {showTracking && (
        <div className="tracking-form">
          <form onSubmit={handleTrackQueue}>
            <input
              type="text"
              placeholder="Enter your ticket number (e.g., A45)"
              value={ticketNumber}
              onChange={(e) => setTicketNumber(e.target.value.toUpperCase())}
            />
            <button type="submit">Track</button>
          </form>
        </div>
      )}

      {/* Affichage du ticket suivi */}
      {trackingTicket && (
        <div className="tracking-result">
          <h3>Ticket {trackingTicket.number}</h3>
          <div className="tracking-details">
            <p><strong>Service:</strong> {trackingTicket.service}</p>
            <p><strong>Created at:</strong> {new Date(trackingTicket.created_at).toLocaleTimeString()}</p>
            <p><strong>Estimated wait:</strong> 12 minutes</p>
            <p><strong>Position:</strong> 5th</p>
            <p><strong>Ahead of you:</strong> 4 people</p>
          </div>
        </div>
      )}

      {/* Statistiques en temps r√©el */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-value">{stats.total_waiting}</div>
          <div className="stat-label">En attente</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{stats.active_counters}</div>
          <div className="stat-label">Guichets ouverts</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{stats.serving_now}</div>
          <div className="stat-label">En cours</div>
        </div>
        <div className="stat-card">
          <div className="stat-value">{stats.served_today}</div>
          <div className="stat-label">Servis aujourd'hui</div>
        </div>
      </div>

      {/* Derni√®re notification */}
      <div className="notification-card">
        <div className="notification-icon">üîî</div>
        <div className="notification-content">
          <p>Il reste 3 personnes avant vous. Pr√©parez vos documents.</p>
          <button className="refresh-btn">Actualiser</button>
        </div>
      </div>

      {/* Liste des services */}
      <div className="services-section">
        <h3>Choose Your Service</h3>
        <div className="services-mini-grid">
          <button className="service-mini-btn" onClick={() => navigate('/create-ticket?service=cash')}>
            Cash Operations
          </button>
          <button className="service-mini-btn" onClick={() => navigate('/create-ticket?service=customer')}>
            Customer Service
          </button>
          <button className="service-mini-btn" onClick={() => navigate('/create-ticket?service=corporate')}>
            Corporate / VIP
          </button>
          <button className="service-mini-btn" onClick={() => navigate('/create-ticket?service=cards')}>
            Cards & Digital
          </button>
        </div>
        <button className="virtual-ticket-btn" onClick={() => navigate('/create-ticket')}>
          Get Your Virtual Ticket +
        </button>
      </div>

      {/* Bouton Cancel */}
      <button className="cancel-btn" onClick={() => navigate('/')}>
        Cancel
      </button>

      {/* Navigation inf√©rieure */}
      <div className="bottom-nav">
        <button className={`nav-btn ${activeTab === 'home' ? 'active' : ''}`} onClick={() => setActiveTab('home')}>
          üè† Home
        </button>
        <button className={`nav-btn ${activeTab === 'faq' ? 'active' : ''}`} onClick={() => navigate('/faq')}>
          ‚ùì FAQ
        </button>
        <button className={`nav-btn ${activeTab === 'support' ? 'active' : ''}`} onClick={() => navigate('/support')}>
          üí¨ Contact Support
        </button>
      </div>
    </div>
  );
};

export default Qonnexea;
